package Lec_1;

public class Maximun_of_three_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 90;
		int b = 100;
		int c = 95;
		if (a > b && a > c) {
			System.out.println(a);
		} else if (b > a && b > c) {
			System.out.println(b);
		} else {
			System.out.println(c);
		}
	}

}
